import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import {
    Container,
    Typography,
    Card,
    CardContent,
    CardActions,
    Button,
    Grid,
    Box,
    List,
    ListItem,
    ListItemText,
    AppBar,
    Toolbar,
    IconButton,
} from '@mui/material';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import ScienceIcon from '@mui/icons-material/Science';
import ConstructionIcon from '@mui/icons-material/Construction';
import LanguageIcon from '@mui/icons-material/Language';
import axios from 'axios';
import { LanguageContext } from '../App';

function Dashboard() {
    const navigate = useNavigate();
    const [farmers, setFarmers] = useState([]);
    const [loading, setLoading] = useState(true);
    const { language, toggleLanguage } = useContext(LanguageContext);

    useEffect(() => {
        fetchFarmers();
    }, []);

    const fetchFarmers = async () => {
        try {
            const response = await axios.get('/api/farmers');
            setFarmers(response.data);
        } catch (error) {
            console.error('Error fetching farmers:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box sx={{ minHeight: '100vh', backgroundColor: '#000000' }}>
            <AppBar position="static" sx={{ backgroundColor: '#000000', borderBottom: '1px solid rgba(76, 175, 80, 0.4)' }}>
                <Toolbar>
                    <img
                        src="/logo.png"
                        alt="SoilSense Logo"
                        style={{
                            height: '48px',
                            marginRight: '16px',
                            filter: 'drop-shadow(0 0 10px rgba(76, 175, 80, 0.5))'
                        }}
                    />
                    <Typography
                        variant="h6"
                        component="div"
                        sx={{
                            flexGrow: 1,
                            color: '#4CAF50',
                            fontWeight: 600,
                            textShadow: '0 0 10px rgba(76, 175, 80, 0.5)'
                        }}
                    >
                        SoilSense - Smart Fertilizer Recommendation System
                    </Typography>
                    <IconButton
                        color="inherit"
                        onClick={toggleLanguage}
                        sx={{ color: '#4CAF50', mr: 2 }}
                        title={`Switch to ${language === 'en' ? 'Hindi' : 'English'}`}
                    >
                        <LanguageIcon />
                        <Typography variant="caption" sx={{ ml: 0.5 }}>
                            {language === 'en' ? 'EN' : 'हिं'}
                        </Typography>
                    </IconButton>
                    <Button
                        color="inherit"
                        startIcon={<ConstructionIcon />}
                        onClick={() => navigate('/coming-soon')}
                        sx={{ color: '#FF9800' }}
                    >
                        Coming Soon
                    </Button>
                </Toolbar>
            </AppBar>

            <Container maxWidth="lg" sx={{ mt: 4, pb: 4 }}>
                <Typography
                    variant="h4"
                    gutterBottom
                    className="text-shimmer"
                    sx={{ fontWeight: 'bold', mb: 4 }}
                >
                    {language === 'en' ? 'Dashboard' : 'डैशबोर्ड'}
                </Typography>

                <Grid container spacing={3}>
                    {/* Quick Actions Card */}
                    <Grid item xs={12} md={6}>
                        <Card className="premium-card" sx={{ height: '100%' }}>
                            <CardContent>
                                <Typography variant="h5" gutterBottom sx={{ color: '#4CAF50' }}>
                                    {language === 'en' ? 'Quick Actions' : 'त्वरित कार्रवाई'}
                                </Typography>
                                <Box sx={{ mt: 2 }}>
                                    <Button
                                        variant="contained"
                                        fullWidth
                                        startIcon={<PersonAddIcon />}
                                        onClick={() => navigate('/register-farmer')}
                                        sx={{ mb: 2, backgroundColor: '#4CAF50', '&:hover': { backgroundColor: '#45a049' } }}
                                    >
                                        {language === 'en' ? 'Register New Farmer' : 'नया किसान पंजीकरण'}
                                    </Button>
                                </Box>
                            </CardContent>
                        </Card>
                    </Grid>

                    {/* Recent Farmers Card */}
                    <Grid item xs={12} md={6}>
                        <Card className="premium-card" sx={{ height: '100%' }}>
                            <CardContent>
                                <Typography variant="h5" gutterBottom sx={{ color: '#4CAF50' }}>
                                    Recent Farmers
                                </Typography>
                                {loading ? (
                                    <Typography>Loading...</Typography>
                                ) : farmers.length === 0 ? (
                                    <Typography sx={{ color: '#e0e0e0', mt: 2 }}>
                                        No farmers registered yet. Register a new farmer to get started.
                                    </Typography>
                                ) : (
                                    <List>
                                        {farmers.slice(0, 5).map((farmer) => (
                                            <ListItem
                                                key={farmer.id}
                                                button
                                                onClick={() => navigate(`/soil-data-entry/${farmer.id}`)}
                                                sx={{
                                                    backgroundColor: '#000000',
                                                    mb: 1,
                                                    borderRadius: 1,
                                                    border: '1px solid rgba(76, 175, 80, 0.2)',
                                                    '&:hover': {
                                                        backgroundColor: '#000000',
                                                        borderColor: 'rgba(76, 175, 80, 0.5)',
                                                        boxShadow: '0 0 15px rgba(76, 175, 80, 0.2)'
                                                    }
                                                }}
                                            >
                                                <ListItemText
                                                    primary={farmer.name}
                                                    secondary={`${farmer.village}, ${farmer.district} | Crop: ${farmer.crop_name}`}
                                                    primaryTypographyProps={{ color: '#ffffff' }}
                                                    secondaryTypographyProps={{ color: '#e0e0e0' }}
                                                />
                                            </ListItem>
                                        ))}
                                    </List>
                                )}
                            </CardContent>
                            {farmers.length > 0 && (
                                <CardActions>
                                    <Button size="small" sx={{ color: '#4CAF50' }}>
                                        View All Farmers
                                    </Button>
                                </CardActions>
                            )}
                        </Card>
                    </Grid>

                    {/* System Features Card */}
                    <Grid item xs={12}>
                        <Card className="premium-card">
                            <CardContent>
                                <Typography variant="h5" gutterBottom sx={{ color: '#4CAF50' }}>
                                    System Features
                                </Typography>
                                <Grid container spacing={2} sx={{ mt: 1 }}>
                                    <Grid item xs={12} md={4}>
                                        <Box className="border-glow" sx={{ p: 2, backgroundColor: '#000000', borderRadius: 1 }}>
                                            <ScienceIcon sx={{ fontSize: 40, color: '#4CAF50', mb: 1 }} />
                                            <Typography variant="h6" sx={{ color: '#ffffff' }}>IoT Integration</Typography>
                                            <Typography variant="body2" sx={{ color: '#e0e0e0', mt: 1 }}>
                                                Real-time data from ESP32 sensors (pH, EC, NPK, moisture)
                                            </Typography>
                                        </Box>
                                    </Grid>
                                    <Grid item xs={12} md={4}>
                                        <Box sx={{ p: 2, backgroundColor: '#2a2a2a', borderRadius: 1 }}>
                                            <ScienceIcon sx={{ fontSize: 40, color: '#4CAF50', mb: 1 }} />
                                            <Typography variant="h6" sx={{ color: '#ffffff' }}>ML Recommendations</Typography>
                                            <Typography variant="body2" sx={{ color: '#e0e0e0', mt: 1 }}>
                                                AI-powered fertilizer suggestions based on Random Forest model
                                            </Typography>
                                        </Box>
                                    </Grid>
                                    <Grid item xs={12} md={4}>
                                        <Box sx={{ p: 2, backgroundColor: '#2a2a2a', borderRadius: 1 }}>
                                            <ScienceIcon sx={{ fontSize: 40, color: '#4CAF50', mb: 1 }} />
                                            <Typography variant="h6" sx={{ color: '#ffffff' }}>Soil Health Cards</Typography>
                                            <Typography variant="body2" sx={{ color: '#e0e0e0', mt: 1 }}>
                                                Official reports in PDF, Excel, CSV, and JSON formats
                                            </Typography>
                                        </Box>
                                    </Grid>
                                </Grid>
                            </CardContent>
                        </Card>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
}

export default Dashboard;
