import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
    Container,
    Typography,
    TextField,
    Button,
    Grid,
    MenuItem,
    Card,
    CardContent,
    Box,
    AppBar,
    Toolbar,
    Alert,
} from '@mui/material';
import AgricultureIcon from '@mui/icons-material/Agriculture';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import axios from 'axios';

function FarmerRegistration() {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        name: '',
        mobile: '',
        village: '',
        district: '',
        state: '',
        field_area: '',
        crop_name: '',
        crop_season: 'Kharif',
    });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState(false);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
        setError('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await axios.post('/api/farmers', {
                ...formData,
                field_area: parseFloat(formData.field_area),
            });

            setSuccess(true);
            setTimeout(() => {
                navigate(`/soil-data-entry/${response.data.id}`);
            }, 1500);
        } catch (err) {
            setError(err.response?.data?.detail || 'Error registering farmer');
        }
    };

    return (
        <Box sx={{ minHeight: '100vh', backgroundColor: '#000000' }}>
            <AppBar position="static" sx={{ backgroundColor: '#1a1a1a' }}>
                <Toolbar>
                    <AgricultureIcon sx={{ mr: 2, color: '#4CAF50' }} />
                    <Typography variant="h6" component="div" sx={{ flexGrow: 1, color: '#4CAF50' }}>
                        SoilSense - Farmer Registration
                    </Typography>
                    <Button startIcon={<ArrowBackIcon />} sx={{ color: '#4CAF50' }} onClick={() => navigate('/')}>
                        Back to Dashboard
                    </Button>
                </Toolbar>
            </AppBar>

            <Container maxWidth="md" sx={{ mt: 4, pb: 4 }}>
                <Card sx={{ backgroundColor: '#1a1a1a' }}>
                    <CardContent sx={{ p: 4 }}>
                        <Typography variant="h4" gutterBottom sx={{ color: '#4CAF50', fontWeight: 'bold', mb: 3 }}>
                            Register New Farmer
                        </Typography>

                        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
                        {success && <Alert severity="success" sx={{ mb: 2 }}>Farmer registered successfully! Redirecting...</Alert>}

                        <form onSubmit={handleSubmit}>
                            <Grid container spacing={3}>
                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        label="Farmer Name"
                                        name="name"
                                        value={formData.name}
                                        onChange={handleChange}
                                        required
                                        sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                    />
                                </Grid>

                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        label="Mobile Number"
                                        name="mobile"
                                        value={formData.mobile}
                                        onChange={handleChange}
                                        required
                                        inputProps={{ pattern: '[0-9]{10,15}' }}
                                        sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                    />
                                </Grid>

                                <Grid item xs={12} md={4}>
                                    <TextField
                                        fullWidth
                                        label="Village"
                                        name="village"
                                        value={formData.village}
                                        onChange={handleChange}
                                        required
                                        sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                    />
                                </Grid>

                                <Grid item xs={12} md={4}>
                                    <TextField
                                        fullWidth
                                        label="District"
                                        name="district"
                                        value={formData.district}
                                        onChange={handleChange}
                                        required
                                        sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                    />
                                </Grid>

                                <Grid item xs={12} md={4}>
                                    <TextField
                                        fullWidth
                                        label="State"
                                        name="state"
                                        value={formData.state}
                                        onChange={handleChange}
                                        required
                                        sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                    />
                                </Grid>

                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        label="Field Area (hectares)"
                                        name="field_area"
                                        type="number"
                                        value={formData.field_area}
                                        onChange={handleChange}
                                        required
                                        inputProps={{ step: '0.01', min: '0.01' }}
                                        sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                    />
                                </Grid>

                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        label="Crop Name"
                                        name="crop_name"
                                        value={formData.crop_name}
                                        onChange={handleChange}
                                        required
                                        sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                    />
                                </Grid>

                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        select
                                        label="Crop Season"
                                        name="crop_season"
                                        value={formData.crop_season}
                                        onChange={handleChange}
                                        required
                                        sx={{ backgroundColor: '#2a2a2a' }}
                                    >
                                        <MenuItem value="Kharif">Kharif (Monsoon)</MenuItem>
                                        <MenuItem value="Rabi">Rabi (Winter)</MenuItem>
                                        <MenuItem value="Zaid">Zaid (Summer)</MenuItem>
                                    </TextField>
                                </Grid>

                                <Grid item xs={12}>
                                    <Button
                                        type="submit"
                                        variant="contained"
                                        fullWidth
                                        size="large"
                                        sx={{
                                            backgroundColor: '#4CAF50',
                                            '&:hover': { backgroundColor: '#45a049' },
                                            py: 1.5,
                                            fontSize: '1.1rem'
                                        }}
                                    >
                                        Register Farmer
                                    </Button>
                                </Grid>
                            </Grid>
                        </form>
                    </CardContent>
                </Card>
            </Container>
        </Box>
    );
}

export default FarmerRegistration;
