import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import {
    Container,
    Box,
    Typography,
    Card,
    CardContent,
    Grid,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
    Button,
    Alert,
    AppBar,
    Toolbar,
    Chip
} from '@mui/material';
import DownloadIcon from '@mui/icons-material/Download';
import ReactMarkdown from 'react-markdown';

function RecommendationView() {
    const { recommendationId } = useParams();
    const navigate = useNavigate();
    const [recommendation, setRecommendation] = useState(null);
    const [farmer, setFarmer] = useState(null);
    const [soilData, setSoilData] = useState(null);
    const [soilHealthCard, setSoilHealthCard] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Parse reasoning to extract structured data
    const parseReasoning = (reasoning) => {
        if (!reasoning) return { soilAnalysis: [], nutrients: [], fertilizers: [], notes: [] };

        const lines = reasoning.split('\n');
        const soilAnalysis = [];
        const nutrients = [];
        const fertilizers = [];
        const notes = [];

        let inFertilizerSection = false;

        for (const line of lines) {
            // Extract soil parameters
            if (line.includes('Nitrogen level') || line.includes('Phosphorus level') || line.includes('Potassium level') || line.includes('Soil pH')) {
                const match = line.match(/^- (.+?) \((.+?)\) is (.+?)\. (.+)$/);
                if (match) {
                    const [, param, value, status, note] = match;
                    soilAnalysis.push({ param, value, status, note });
                }
            }

            // Extract recommended nutrients
            if (line.includes('kg/ha)') && (line.includes('Nitrogen') || line.includes('Phosphorus') || line.includes('Potassium'))) {
                const match = line.match(/^- (.+?) \((.+?)\): (.+?) kg\/ha$/);
                if (match) {
                    const [, nutrient, symbol, quantity] = match;
                    nutrients.push({ nutrient, symbol, quantity: parseFloat(quantity) });
                }
            }

            // Extract fertilizer recommendations
            if (line.includes('**Fertilizer Recommendations**')) {
                inFertilizerSection = true;
                continue;
            }

            if (inFertilizerSection && line.includes('|')) {
                // Parse table rows
                const cells = line.split('|').map(c => c.trim()).filter(Boolean);
                if (cells.length >= 4 && cells[0] !== 'Fertilizer' && cells[0] !== '---') {
                    fertilizers.push({
                        name: cells[0],
                        quantity: cells[1],
                        costPerKg: cells[2],
                        totalCost: cells[3]
                    });
                }
            }

            // Extract application notes
            if (line.includes('**Application') || line.includes('Split application') || line.includes('Apply')) {
                notes.push(line.replace(/\*\*/g, ''));
            }
        }

        return { soilAnalysis, nutrients, fertilizers, notes };
    };

    useEffect(() => {
        const fetchRecommendation = async () => {
            try {
                setLoading(true);

                // Fetch recommendation
                const recResponse = await axios.get(`/api/recommendations/${recommendationId}`);
                setRecommendation(recResponse.data);

                // Fetch farmer
                const farmerResponse = await axios.get(`/api/farmers/${recResponse.data.farmer_id}`);
                setFarmer(farmerResponse.data);

                // Fetch soil data
                const soilResponse = await axios.get(`/api/soil-data/${recResponse.data.soil_data_id}`);
                setSoilData(soilResponse.data);

                // Try to fetch soil health card
                try {
                    const shcResponse = await axios.get(`/api/soil-health-card/farmer/${recResponse.data.farmer_id}`);
                    if (shcResponse.data && shcResponse.data.length > 0) {
                        setSoilHealthCard(shcResponse.data[0]);
                    }
                } catch (shcError) {
                    console.log('No soil health card found:', shcError);
                }

                setLoading(false);
            } catch (err) {
                console.error('Error fetching data:', err);
                setError(err.response?.data?.detail || 'Failed to load recommendation');
                setLoading(false);
            }
        };

        fetchRecommendation();
    }, [recommendationId]);

    const handleDownload = async (format) => {
        if (!soilHealthCard) {
            alert('Soil Health Card not generated yet');
            return;
        }
        try {
            const response = await axios.get(
                `/api/soil-health-card/${soilHealthCard.card_id}/download/${format}`,
                { responseType: 'blob' }
            );
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `soil_health_card_${soilHealthCard.card_id}.${format === 'excel' ? 'xlsx' : format}`);
            document.body.appendChild(link);
            link.click();
            link.remove();
        } catch (err) {
            console.error('Download error:', err);
            alert('Failed to download file');
        }
    };

    if (loading) {
        return (
            <Box sx={{ minHeight: '100vh', backgroundColor: '#000', p: 4 }}>
                <Typography sx={{ color: '#fff' }}>Loading...</Typography>
            </Box>
        );
    }

    if (error) {
        return (
            <Box sx={{ minHeight: '100vh', backgroundColor: '#000', p: 4 }}>
                <Alert severity="error">{error}</Alert>
            </Box>
        );
    }

    const parsedData = recommendation ? parseReasoning(recommendation.reasoning) : null;

    return (
        <Box sx={{ minHeight: '100vh', backgroundColor: '#000000' }}>
            <AppBar position="static" sx={{ backgroundColor: '#000000', borderBottom: '1px solid #333' }}>
                <Toolbar>
                    <img src="/logo.png" alt="SoilSense Logo" style={{ height: '60px', marginRight: '16px' }} />
                    <Typography variant="h6" component="div" sx={{ flexGrow: 1 }} className="gradient-text">
                        SoilSense - Fertilizer Recommendation
                    </Typography>
                    <Button sx={{ color: '#4CAF50' }} onClick={() => navigate('/')}>
                        Back to Dashboard
                    </Button>
                </Toolbar>
            </AppBar>

            <Container maxWidth="lg" sx={{ mt: 4, pb: 4 }}>
                {/* Farmer Info */}
                {farmer && (
                    <Card sx={{ mb: 3, backgroundColor: '#000000', border: '1px solid #333' }}>
                        <CardContent>
                            <Grid container spacing={2}>
                                <Grid item xs={12} md={6}>
                                    <Typography variant="h6" sx={{ color: '#4CAF50' }}>
                                        {farmer.name}
                                    </Typography>
                                    <Typography variant="body2" sx={{ color: '#e0e0e0' }}>
                                        {farmer.village}, {farmer.district}, {farmer.state}
                                    </Typography>
                                </Grid>
                                <Grid item xs={12} md={6}>
                                    <Typography variant="body2" sx={{ color: '#e0e0e0' }}>
                                        <strong>Crop:</strong> {farmer.crop_name} ({farmer.crop_season})
                                    </Typography>
                                    <Typography variant="body2" sx={{ color: '#e0e0e0' }}>
                                        <strong>Field Area:</strong> {farmer.field_area} hectares
                                    </Typography>
                                </Grid>
                            </Grid>
                        </CardContent>
                    </Card>
                )}

                {/* Soil Health Card Downloads */}
                {soilHealthCard && (
                    <Card sx={{ mb: 3, backgroundColor: '#000000', border: '1px solid #333' }}>
                        <CardContent>
                            <Typography variant="h6" sx={{ color: '#4CAF50', mb: 2 }}>
                                Download Soil Health Card
                            </Typography>
                            <Grid container spacing={2}>
                                <Grid item xs={12} sm={4}>
                                    <Button
                                        variant="contained"
                                        fullWidth
                                        sx={{ backgroundColor: '#4CAF50', color: '#000', fontWeight: 'bold' }}
                                        startIcon={<DownloadIcon />}
                                        onClick={() => handleDownload('pdf')}
                                    >
                                        Download PDF
                                    </Button>
                                </Grid>
                                <Grid item xs={12} sm={4}>
                                    <Button
                                        variant="contained"
                                        fullWidth
                                        sx={{ backgroundColor: '#81C784', color: '#000', fontWeight: 'bold' }}
                                        startIcon={<DownloadIcon />}
                                        onClick={() => handleDownload('excel')}
                                    >
                                        Download Excel
                                    </Button>
                                </Grid>
                                <Grid item xs={12} sm={4}>
                                    <Button
                                        variant="outlined"
                                        fullWidth
                                        sx={{ borderColor: '#4CAF50', color: '#4CAF50' }}
                                        startIcon={<DownloadIcon />}
                                        onClick={() => handleDownload('csv')}
                                    >
                                        Download CSV
                                    </Button>
                                </Grid>
                            </Grid>
                        </CardContent>
                    </Card>
                )}

                {/* Fertilizer Recommendation */}
                {recommendation && parsedData && (
                    <Card sx={{ mb: 3, backgroundColor: '#000000', border: '1px solid #333' }}>
                        <CardContent>
                            <Typography variant="h5" gutterBottom sx={{ color: '#4CAF50', mb: 3 }}>
                                 Analysis & Recommendations
                            </Typography>

                            {/* Soil Analysis Table */}
                            {parsedData.soilAnalysis.length > 0 && (
                                <>
                                    <Typography variant="h6" sx={{ color: '#8bc34a', mb: 2, mt: 3 }}>
                                        1. Soil Analysis
                                    </Typography>
                                    <TableContainer component={Paper} sx={{ mb: 3, backgroundColor: '#000000', border: '1px solid #333' }}>
                                        <Table>
                                            <TableHead>
                                                <TableRow>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                        Parameter
                                                    </TableCell>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                        Value
                                                    </TableCell>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                        Status
                                                    </TableCell>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                        Assessment
                                                    </TableCell>
                                                </TableRow>
                                            </TableHead>
                                            <TableBody>
                                                {parsedData.soilAnalysis.map((item, idx) => (
                                                    <TableRow key={idx}>
                                                        <TableCell sx={{ color: '#e0e0e0', fontWeight: 'bold' }}>
                                                            {item.param}
                                                        </TableCell>
                                                        <TableCell sx={{ color: '#e0e0e0' }}>
                                                            {item.value}
                                                        </TableCell>
                                                        <TableCell>
                                                            <Chip
                                                                label={item.status}
                                                                size="small"
                                                                sx={{
                                                                    backgroundColor: item.status.includes('LOW') ? '#f44336' :
                                                                        item.status.includes('MODERATE') ? '#ff9800' :
                                                                            item.status.includes('HIGH') ? '#4caf50' : '#2196f3',
                                                                    color: '#000',
                                                                    fontWeight: 'bold'
                                                                }}
                                                            />
                                                        </TableCell>
                                                        <TableCell sx={{ color: '#b0b0b0', fontSize: '0.9rem' }}>
                                                            {item.note}
                                                        </TableCell>
                                                    </TableRow>
                                                ))}
                                            </TableBody>
                                        </Table>
                                    </TableContainer>
                                </>
                            )}

                            {/* Nutrient Requirements Table */}
                            {parsedData.nutrients.length > 0 && (
                                <>
                                    <Typography variant="h6" sx={{ color: '#8bc34a', mb: 2, mt: 3 }}>
                                        2. Recommended Nutrients
                                    </Typography>
                                    <TableContainer component={Paper} sx={{ mb: 3, backgroundColor: '#000000', border: '1px solid #333' }}>
                                        <Table>
                                            <TableHead>
                                                <TableRow>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                        Nutrient
                                                    </TableCell>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                        Chemical Form
                                                    </TableCell>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                        Required (kg/ha)
                                                    </TableCell>
                                                </TableRow>
                                            </TableHead>
                                            <TableBody>
                                                {parsedData.nutrients.map((item, idx) => (
                                                    <TableRow key={idx}>
                                                        <TableCell sx={{ color: '#e0e0e0', fontWeight: 'bold' }}>
                                                            {item.nutrient}
                                                        </TableCell>
                                                        <TableCell sx={{ color: '#e0e0e0' }}>
                                                            {item.symbol}
                                                        </TableCell>
                                                        <TableCell sx={{ color: '#4caf50', fontWeight: 'bold', fontSize: '1.1rem' }}>
                                                            {item.quantity.toFixed(1)}
                                                        </TableCell>
                                                    </TableRow>
                                                ))}
                                            </TableBody>
                                        </Table>
                                    </TableContainer>
                                </>
                            )}

                            {/* Fertilizer Breakdown Table */}
                            {parsedData.fertilizers.length > 0 && (
                                <>
                                    <Typography variant="h6" sx={{ color: '#8bc34a', mb: 2, mt: 3 }}>
                                        3. Fertilizer Breakdown & Cost
                                    </Typography>
                                    <TableContainer component={Paper} sx={{ mb: 3, backgroundColor: '#000000', border: '1px solid #333' }}>
                                        <Table>
                                            <TableHead>
                                                <TableRow>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                        Fertilizer
                                                    </TableCell>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                        Quantity
                                                    </TableCell>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                        Cost/kg
                                                    </TableCell>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                        Total Cost
                                                    </TableCell>
                                                </TableRow>
                                            </TableHead>
                                            <TableBody>
                                                {parsedData.fertilizers.map((item, idx) => (
                                                    <TableRow key={idx}>
                                                        <TableCell sx={{ color: '#e0e0e0', fontWeight: 'bold' }}>
                                                            {item.name}
                                                        </TableCell>
                                                        <TableCell sx={{ color: '#e0e0e0' }}>
                                                            {item.quantity}
                                                        </TableCell>
                                                        <TableCell sx={{ color: '#e0e0e0' }}>
                                                            {item.costPerKg}
                                                        </TableCell>
                                                        <TableCell sx={{ color: '#4caf50', fontWeight: 'bold', fontSize: '1.1rem' }}>
                                                            {item.totalCost}
                                                        </TableCell>
                                                    </TableRow>
                                                ))}
                                            </TableBody>
                                        </Table>
                                    </TableContainer>
                                </>
                            )}

                            {/* Application Notes */}
                            {parsedData.notes.length > 0 && (
                                <Box sx={{ mt: 3, p: 2, backgroundColor: '#1a1a1a', border: '1px solid #4CAF50', borderRadius: 1 }}>
                                    <Typography variant="h6" sx={{ color: '#4CAF50', mb: 2 }}>
                                         Application Guidelines
                                    </Typography>
                                    {parsedData.notes.map((note, idx) => (
                                        <Typography key={idx} variant="body2" sx={{ color: '#e0e0e0', mb: 1 }}>
                                             {note}
                                        </Typography>
                                    ))}
                                </Box>
                            )}

                            {/* Fallback: Show original reasoning if parsing failed */}
                            {parsedData.fertilizers.length === 0 && (
                                <Box sx={{ mt: 3, p: 2, backgroundColor: '#000000', border: '1px solid #333', borderRadius: 1 }}>
                                    <Typography variant="h6" sx={{ color: '#4CAF50', mb: 2 }}>
                                        Analysis & Reasoning
                                    </Typography>
                                    <Box sx={{
                                        color: '#e0e0e0',
                                        '& p': { mb: 1, lineHeight: 1.6 },
                                        '& ul': { pl: 2 },
                                        '& li': { mb: 0.5 },
                                        '& a': { color: '#64b5f6', textDecoration: 'none', '&:hover': { textDecoration: 'underline' } },
                                        '& strong': { color: '#81C784' },
                                        '& table': { width: '100%', borderCollapse: 'collapse', mt: 2, mb: 2 },
                                        '& th, & td': { border: '1px solid #333', padding: '8px', textAlign: 'left' },
                                        '& th': { backgroundColor: '#4CAF50', color: '#000', fontWeight: 'bold' }
                                    }}>
                                        <ReactMarkdown>{recommendation.reasoning}</ReactMarkdown>
                                    </Box>
                                </Box>
                            )}
                        </CardContent>
                    </Card>
                )}
            </Container>
        </Box>
    );
}

export default RecommendationView;
