import React, { useState, createContext } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme, CssBaseline } from '@mui/material';
import Dashboard from './pages/Dashboard';
import FarmerRegistration from './pages/FarmerRegistration';
import SoilDataEntry from './pages/SoilDataEntry';
import RecommendationView from './pages/RecommendationView';
import ComingSoon from './pages/ComingSoon';

// Language Context
export const LanguageContext = createContext();

// Pure Black Premium Theme Configuration
const darkTheme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#4CAF50', // Green for agriculture theme
    },
    secondary: {
      main: '#81C784',
    },
    background: {
      default: '#000000',
      paper: '#000000',
    },
    text: {
      primary: '#ffffff',
      secondary: '#e0e0e0',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          backgroundColor: 'transparent', // Let CSS handle the glass effect
          border: 'none', // CSS handles border
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          backgroundColor: 'transparent',
          backgroundImage: 'none',
        },
      },
    },
    MuiAppBar: {
      styleOverrides: {
        root: {
          backgroundColor: '#000000',
          borderBottom: '1px solid rgba(76, 175, 80, 0.3)',
        },
      },
    },
  },
});

function App() {
  const [language, setLanguage] = useState('en');

  return (
    <LanguageContext.Provider value={{ language, setLanguage }}>
      <ThemeProvider theme={darkTheme}>
        <CssBaseline />
        <Router>
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/register-farmer" element={<FarmerRegistration />} />
            <Route path="/soil-data-entry/:farmerId" element={<SoilDataEntry />} />
            <Route path="/recommendation/:farmerId/:soilDataId" element={<RecommendationView />} />
            <Route path="/coming-soon" element={<ComingSoon />} />
          </Routes>
        </Router>
      </ThemeProvider>
    </LanguageContext.Provider>
  );
}

export default App;
