import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import {
    Box, AppBar, Toolbar, Typography, Card, CardContent,
    Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
    Paper, Button, Chip, Divider, Alert
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import DownloadIcon from '@mui/icons-material/Download';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ReactMarkdown from 'react-markdown';

const RecommendationView = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const [recommendation, setRecommendation] = useState(location.state?.recommendation || null);
    const [fertilizers, setFertilizers] = useState([]);

    useEffect(() => {
        if (!recommendation) {
            navigate('/dashboard');
            return;
        }

        // Parse fertilizer recommendations from reasoning
        parseFertilizerData(recommendation.reasoning);
    }, [recommendation, navigate]);

    const parseFertilizerData = (reasoning) => {
        if (!reasoning) return;

        // Extract fertilizer table data
        const fertilizerData = [];
        const lines = reasoning.split('\n');

        for (let i = 0; i < lines.length; i++) {
            const line = lines[i].trim();

            // Look for fertilizer entries (e.g., "DAP (Diammonium Phosphate):")
            if (line.includes('DAP') || line.includes('MOP') || line.includes('Urea')) {
                const fertName = line.replace(':', '').trim();
                const details = {};

                // Parse next few lines for quantity and cost
                for (let j = i + 1; j < i + 5 && j < lines.length; j++) {
                    const detailLine = lines[j].trim();
                    if (detailLine.startsWith('- Quantity:')) {
                        details.quantity = detailLine.replace('- Quantity:', '').trim();
                    } else if (detailLine.startsWith('- Cost:')) {
                        details.cost = detailLine.replace('- Cost:', '').trim();
                    }
                }

                if (details.quantity && details.cost) {
                    fertilizerData.push({
                        name: fertName,
                        quantity: details.quantity,
                        cost: details.cost
                    });
                }
            }
        }

        setFertilizers(fertilizerData);
    };

    const getSoilAnalysisSection = () => {
        if (!recommendation?.reasoning) return '';
        const lines = recommendation.reasoning.split('\n');
        const analysisLines = [];
        let inAnalysis = false;

        for (const line of lines) {
            if (line.includes('**Soil Analysis')) {
                inAnalysis = true;
            } else if (line.includes('**Recommended Nutrients')) {
                break;
            } else if (inAnalysis && line.trim()) {
                analysisLines.push(line);
            }
        }

        return analysisLines.join('\n');
    };

    const getNutrientRequirements = () => {
        if (!recommendation) return null;
        return {
            nitrogen: recommendation.required_nitrogen?.toFixed(1) || 'N/A',
            phosphorus: recommendation.required_phosphorus?.toFixed(1) || 'N/A',
            potassium: recommendation.required_potassium?.toFixed(1) || 'N/A'
        };
    };

    const handleDownloadPDF = async () => {
        try {
            // TODO: Implement PDF generation with Analysis & Reasoning
            alert('PDF download feature will be implemented');
        } catch (error) {
            console.error('Error downloading PDF:', error);
        }
    };

    const handleBack = () => {
        navigate('/dashboard');
    };

    if (!recommendation) {
        return (
            <Box sx={{ p: 3 }}>
                <Alert severity="error">No recommendation data found. Redirecting to dashboard...</Alert>
            </Box>
        );
    }

    const nutrients = getNutrientRequirements();

    return (
        <Box sx={{ minHeight: '100vh', backgroundColor: '#000000' }}>
            <AppBar position="static" sx={{ backgroundColor: '#000000', borderBottom: '1px solid #333' }}>
                <Toolbar>
                    <img src="/logo.png" alt="SoilSense Logo" style={{ height: '60px', marginRight: '16px' }} />
                    <Typography variant="h6" component="div" sx={{ flexGrow: 1 }} className="gradient-text">
                        SoilSense - Fertilizer Recommendation
                    </Typography>
                    <Button
                        color="inherit"
                        startIcon={<ArrowBackIcon />}
                        onClick={handleBack}
                        sx={{ color: '#4CAF50' }}
                    >
                        Back to Dashboard
                    </Button>
                </Toolbar>
            </AppBar>

            <Box sx={{ p: 4, maxWidth: '1200px', margin: '0 auto' }}>
                {/* Header Card */}
                <Card sx={{ mb: 3, backgroundColor: '#000000', border: '1px solid #333' }}>
                    <CardContent>
                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                            <CheckCircleIcon sx={{ color: '#4CAF50', fontSize: 40, mr: 2 }} />
                            <Box>
                                <Typography variant="h4" sx={{ color: '#4CAF50', fontWeight: 'bold' }}>
                                    Recommendation Generated
                                </Typography>
                                <Typography variant="body2" sx={{ color: '#b0b0b0' }}>
                                    ID: #{recommendation.id} ‚Ä¢ Generated: {new Date(recommendation.created_at).toLocaleDateString()}
                                </Typography>
                            </Box>
                        </Box>

                        <Button
                            variant="contained"
                            color="primary"
                            startIcon={<DownloadIcon />}
                            onClick={handleDownloadPDF}
                            sx={{ mt: 2 }}
                        >
                            Download Soil Health Card
                        </Button>
                    </CardContent>
                </Card>

                {/* Soil Analysis */}
                <Card sx={{ mb: 3, backgroundColor: '#000000', border: '1px solid #333' }} className="premium-card">
                    <CardContent>
                        <Typography variant="h5" sx={{ color: '#4CAF50', mb: 2, fontWeight: 'bold' }}>
                            üìä Soil Analysis
                        </Typography>
                        <Box sx={{ color: '#fff' }}>
                            <ReactMarkdown>{getSoilAnalysisSection()}</ReactMarkdown>
                        </Box>
                    </CardContent>
                </Card>

                {/* Nutrient Requirements Table */}
                <Card sx={{ mb: 3, backgroundColor: '#000000', border: '1px solid #333' }} className="premium-card">
                    <CardContent>
                        <Typography variant="h5" sx={{ color: '#4CAF50', mb: 2, fontWeight: 'bold' }}>
                            üå± Recommended Nutrients (kg/ha)
                        </Typography>
                        <TableContainer component={Paper} sx={{ backgroundColor: '#000000', border: '1px solid #333' }}>
                            <Table>
                                <TableHead>
                                    <TableRow sx={{ backgroundColor: '#1a1a1a' }}>
                                        <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold', fontSize: '1.1rem' }}>Nutrient</TableCell>
                                        <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold', fontSize: '1.1rem' }}>Required (kg/ha)</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    <TableRow sx={{ '&:hover': { backgroundColor: '#111' } }}>
                                        <TableCell sx={{ color: '#fff', fontSize: '1rem' }}>Nitrogen (N)</TableCell>
                                        <TableCell align="right" sx={{ color: '#fff', fontSize: '1rem', fontWeight: 'bold' }}>{nutrients.nitrogen}</TableCell>
                                    </TableRow>
                                    <TableRow sx={{ '&:hover': { backgroundColor: '#111' } }}>
                                        <TableCell sx={{ color: '#fff', fontSize: '1rem' }}>Phosphorus (P‚ÇÇO‚ÇÖ)</TableCell>
                                        <TableCell align="right" sx={{ color: '#fff', fontSize: '1rem', fontWeight: 'bold' }}>{nutrients.phosphorus}</TableCell>
                                    </TableRow>
                                    <TableRow sx={{ '&:hover': { backgroundColor: '#111' } }}>
                                        <TableCell sx={{ color: '#fff', fontSize: '1rem' }}>Potassium (K‚ÇÇO)</TableCell>
                                        <TableCell align="right" sx={{ color: '#fff', fontSize: '1rem', fontWeight: 'bold' }}>{nutrients.potassium}</TableCell>
                                    </TableRow>
                                </TableBody>
                            </Table>
                        </TableContainer>
                    </CardContent>
                </Card>

                {/* Fertilizer Recommendations Table */}
                <Card sx={{ mb: 3, backgroundColor: '#000000', border: '1px solid #333' }} className="premium-card">
                    <CardContent>
                        <Typography variant="h5" sx={{ color: '#4CAF50', mb: 2, fontWeight: 'bold' }}>
                            üíä Fertilizer Recommendations
                        </Typography>
                        {fertilizers.length > 0 ? (
                            <TableContainer component={Paper} sx={{ backgroundColor: '#000000', border: '1px solid #333' }}>
                                <Table>
                                    <TableHead>
                                        <TableRow sx={{ backgroundColor: '#1a1a1a' }}>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold', fontSize: '1.1rem' }}>Fertilizer</TableCell>
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold', fontSize: '1.1rem' }}>Quantity</TableCell>
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold', fontSize: '1.1rem' }}>Estimated Cost</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {fertilizers.map((fert, index) => (
                                            <TableRow key={index} sx={{ '&:hover': { backgroundColor: '#111' } }}>
                                                <TableCell sx={{ color: '#fff', fontSize: '1rem' }}>
                                                    <strong>{fert.name}</strong>
                                                </TableCell>
                                                <TableCell align="right" sx={{ color: '#fff', fontSize: '1rem' }}>{fert.quantity}</TableCell>
                                                <TableCell align="right" sx={{ color: '#4CAF50', fontSize: '1rem', fontWeight: 'bold' }}>{fert.cost}</TableCell>
                                            </TableRow>
                                        ))}
                                        <TableRow sx={{ backgroundColor: '#1a1a1a' }}>
                                            <TableCell sx={{ color: '#fff', fontWeight: 'bold', fontSize: '1.1rem' }} colSpan={2}>
                                                Total Estimated Cost
                                            </TableCell>
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold', fontSize: '1.2rem' }}>
                                                ‚Çπ{recommendation.estimated_cost?.toFixed(2) || 'N/A'}
                                            </TableCell>
                                        </TableRow>
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        ) : (
                            <Box sx={{ color: '#fff' }}>
                                <ReactMarkdown>{recommendation.reasoning}</ReactMarkdown>
                            </Box>
                        )}
                    </CardContent>
                </Card>

                {/* Application Method */}
                <Card sx={{ mb: 3, backgroundColor: '#000000', border: '1px solid #333' }} className="premium-card">
                    <CardContent>
                        <Typography variant="h5" sx={{ color: '#4CAF50', mb: 2, fontWeight: 'bold' }}>
                            üìù Application Guidelines
                        </Typography>
                        <Box sx={{ mb: 2 }}>
                            <Typography variant="subtitle1" sx={{ color: '#8bc34a', fontWeight: 'bold', mb: 1 }}>
                                Timing:
                            </Typography>
                            <Typography sx={{ color: '#fff', ml: 2 }}>
                                {recommendation.application_timing || 'Apply as per crop growth stage'}
                            </Typography>
                        </Box>
                        <Box>
                            <Typography variant="subtitle1" sx={{ color: '#8bc34a', fontWeight: 'bold', mb: 1 }}>
                                Method:
                            </Typography>
                            <Typography sx={{ color: '#fff', ml: 2 }}>
                                {recommendation.application_method || 'Apply by broadcasting and incorporate into soil'}
                            </Typography>
                        </Box>
                    </CardContent>
                </Card>
            </Box>
        </Box>
    );
};

export default RecommendationView;
