import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
    Container,
    Typography,
    Card,
    CardContent,
    TextField,
    Button,
    Grid,
    Box,
    AppBar,
    Toolbar,
    MenuItem,
    Alert,
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import axios from 'axios';
import logo from '../../../logo.png';

function FarmerRegistration() {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        name: '',
        mobile: '',
        village: '',
        district: '',
        state: '',
        field_area: '',
        crop_name: '',
        crop_season: '',
    });
    const [error, setError] = useState('');
    const [success, setSuccess] = useState(false);

    const seasons = ['Kharif', 'Rabi', 'Zaid'];

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await axios.post('/api/farmers', {
                ...formData,
                field_area: parseFloat(formData.field_area),
            });

            setSuccess(true);
            setTimeout(() => {
                navigate(`/soil-data-entry/${response.data.id}`);
            }, 1500);
        } catch (err) {
            setError(err.response?.data?.detail || 'Failed to register farmer. Please try again.');
        }
    };

    return (
        <Box sx={{ minHeight: '100vh', backgroundColor: '#000000' }}>
            <AppBar position="static">
                <Toolbar>
                    <img
                        src={logo}
                        alt="SoilSense Logo"
                        style={{
                            height: '48px',
                            marginRight: '16px',
                            filter: 'drop-shadow(0 0 10px rgba(76, 175, 80, 0.5))'
                        }}
                    />
                    <Typography
                        variant="h6"
                        component="div"
                        sx={{
                            flexGrow: 1,
                            fontWeight: 600,
                            textShadow: '0 0 10px rgba(76, 175, 80, 0.5)'
                        }}
                    >
                        <span className="text-shimmer">Farmer Registration</span>
                    </Typography>
                    <Button
                        color="inherit"
                        startIcon={<ArrowBackIcon />}
                        onClick={() => navigate('/')}
                        sx={{ borderColor: 'rgba(76, 175, 80, 0.5)' }}
                    >
                        Back to Dashboard
                    </Button>
                </Toolbar>
            </AppBar>

            <Container maxWidth="md" sx={{ mt: 4, pb: 4 }}>
                <Card className="premium-card">
                    <CardContent sx={{ p: 4 }}>
                        <Typography
                            variant="h4"
                            gutterBottom
                            className="text-shimmer"
                            sx={{ mb: 3, textAlign: 'center' }}
                        >
                            Register New Farmer
                        </Typography>

                        {error && (
                            <Alert severity="error" sx={{ mb: 2, backgroundColor: 'rgba(211, 47, 47, 0.1)' }}>
                                {error}
                            </Alert>
                        )}

                        {success && (
                            <Alert severity="success" sx={{ mb: 2, backgroundColor: 'rgba(76, 175, 80, 0.1)' }}>
                                Farmer registered successfully! Redirecting to soil data entry...
                            </Alert>
                        )}

                        <form onSubmit={handleSubmit}>
                            <Grid container spacing={3}>
                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        required
                                        label="Farmer Name"
                                        name="name"
                                        value={formData.name}
                                        onChange={handleChange}
                                    />
                                </Grid>

                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        required
                                        label="Mobile Number"
                                        name="mobile"
                                        value={formData.mobile}
                                        onChange={handleChange}
                                        inputProps={{ pattern: '[0-9]{10}', maxLength: 10 }}
                                    />
                                </Grid>

                                <Grid item xs={12} md={4}>
                                    <TextField
                                        fullWidth
                                        required
                                        label="Village"
                                        name="village"
                                        value={formData.village}
                                        onChange={handleChange}
                                    />
                                </Grid>

                                <Grid item xs={12} md={4}>
                                    <TextField
                                        fullWidth
                                        required
                                        label="District"
                                        name="district"
                                        value={formData.district}
                                        onChange={handleChange}
                                    />
                                </Grid>

                                <Grid item xs={12} md={4}>
                                    <TextField
                                        fullWidth
                                        required
                                        label="State"
                                        name="state"
                                        value={formData.state}
                                        onChange={handleChange}
                                    />
                                </Grid>

                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        required
                                        label="Field Area (hectares)"
                                        name="field_area"
                                        type="number"
                                        value={formData.field_area}
                                        onChange={handleChange}
                                        inputProps={{ min: 0, step: 0.1 }}
                                    />
                                </Grid>

                                <Grid item xs={12} md={6}>
                                    <TextField
                                        fullWidth
                                        required
                                        label="Crop Name"
                                        name="crop_name"
                                        value={formData.crop_name}
                                        onChange={handleChange}
                                    />
                                </Grid>

                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        required
                                        select
                                        label="Crop Season"
                                        name="crop_season"
                                        value={formData.crop_season}
                                        onChange={handleChange}
                                    >
                                        {seasons.map((season) => (
                                            <MenuItem key={season} value={season}>
                                                {season}
                                            </MenuItem>
                                        ))}
                                    </TextField>
                                </Grid>

                                <Grid item xs={12}>
                                    <Button
                                        type="submit"
                                        variant="contained"
                                        size="large"
                                        fullWidth
                                        startIcon={<PersonAddIcon />}
                                        className="glow-green"
                                        sx={{
                                            mt: 2,
                                            py: 1.5,
                                            fontSize: '1.1rem',
                                            backgroundColor: '#4CAF50',
                                            '&:hover': {
                                                backgroundColor: '#45a049',
                                                boxShadow: '0 0 20px rgba(76, 175, 80, 0.5)',
                                            },
                                        }}
                                    >
                                        Register Farmer
                                    </Button>
                                </Grid>
                            </Grid>
                        </form>
                    </CardContent>
                </Card>
            </Container>
        </Box>
    );
}

export default FarmerRegistration;
