import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
    Container,
    Typography,
    TextField,
    Button,
    Grid,
    Card,
    CardContent,
    Box,
    AppBar,
    Toolbar,
    Tabs,
    Tab,
    Alert,
    CircularProgress,
} from '@mui/material';
import AgricultureIcon from '@mui/icons-material/Agriculture';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import axios from 'axios';

function SoilDataEntry() {
    const { farmerId } = useParams();
    const navigate = useNavigate();
    const [tabValue, setTabValue] = useState(0);
    const [farmer, setFarmer] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    // Manual entry state
    const [manualData, setManualData] = useState({
        nitrogen: '',
        phosphorus: '',
        potassium: '',
        ph: '',
        ec: '',
        moisture: '',
        organic_carbon: '',
    });

    // File upload state
    const [selectedFile, setSelectedFile] = useState(null);

    useEffect(() => {
        fetchFarmer();
    }, [farmerId]);

    const fetchFarmer = async () => {
        try {
            const response = await axios.get(`/api/farmers/${farmerId}`);
            setFarmer(response.data);
        } catch (err) {
            setError('Error loading farmer data');
        }
    };

    const handleManualSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const response = await axios.post('/api/soil-data/manual', {
                farmer_id: parseInt(farmerId),
                nitrogen: manualData.nitrogen ? parseFloat(manualData.nitrogen) : null,
                phosphorus: manualData.phosphorus ? parseFloat(manualData.phosphorus) : null,
                potassium: manualData.potassium ? parseFloat(manualData.potassium) : null,
                ph: manualData.ph ? parseFloat(manualData.ph) : null,
                ec: manualData.ec ? parseFloat(manualData.ec) : null,
                moisture: manualData.moisture ? parseFloat(manualData.moisture) : null,
                organic_carbon: manualData.organic_carbon ? parseFloat(manualData.organic_carbon) : null,
            });

            setSuccess('Soil data saved successfully!');
            setTimeout(() => {
                navigate(`/recommendation/${farmerId}/${response.data.id}`);
            }, 1500);
        } catch (err) {
            setError(err.response?.data?.detail || 'Error saving soil data');
        } finally {
            setLoading(false);
        }
    };

    const handleFileUpload = async (e) => {
        e.preventDefault();
        if (!selectedFile) {
            setError('Please select a file');
            return;
        }

        setLoading(true);
        setError('');

        const formData = new FormData();
        formData.append('file', selectedFile);

        try {
            const response = await axios.post(`/api/soil-data/upload/${farmerId}`, formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });

            setSuccess('File uploaded and data extracted successfully!');
            setTimeout(() => {
                navigate(`/recommendation/${farmerId}/${response.data.id}`);
            }, 1500);
        } catch (err) {
            setError(err.response?.data?.detail || 'Error uploading file');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box sx={{ minHeight: '100vh', backgroundColor: '#000000' }}>
            <AppBar position="static" sx={{ backgroundColor: '#1a1a1a' }}>
                <Toolbar>
                    <AgricultureIcon sx={{ mr: 2, color: '#4CAF50' }} />
                    <Typography variant="h6" component="div" sx={{ flexGrow: 1, color: '#4CAF50' }}>
                        SoilSense - Soil Data Entry
                    </Typography>
                    <Button sx={{ color: '#4CAF50' }} onClick={() => navigate('/')}>
                        Back to Dashboard
                    </Button>
                </Toolbar>
            </AppBar>

            <Container maxWidth="md" sx={{ mt: 4, pb: 4 }}>
                {farmer && (
                    <Card sx={{ mb: 3, backgroundColor: '#1a1a1a' }}>
                        <CardContent>
                            <Typography variant="h6" sx={{ color: '#4CAF50' }}>
                                Farmer: {farmer.name}
                            </Typography>
                            <Typography variant="body2" sx={{ color: '#e0e0e0' }}>
                                {farmer.village}, {farmer.district} | Crop: {farmer.crop_name} ({farmer.crop_season})
                            </Typography>
                        </CardContent>
                    </Card>
                )}

                <Card sx={{ backgroundColor: '#1a1a1a' }}>
                    <Tabs
                        value={tabValue}
                        onChange={(e, newValue) => setTabValue(newValue)}
                        sx={{ borderBottom: 1, borderColor: '#2a2a2a' }}
                        TabIndicatorProps={{ style: { backgroundColor: '#4CAF50' } }}
                    >
                        <Tab label="Manual Entry" sx={{ color: '#e0e0e0', '&.Mui-selected': { color: '#4CAF50' } }} />
                        <Tab label="File Upload" sx={{ color: '#e0e0e0', '&.Mui-selected': { color: '#4CAF50' } }} />
                    </Tabs>

                    <CardContent sx={{ p: 4 }}>
                        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
                        {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}

                        {/* Manual Entry Tab */}
                        {tabValue === 0 && (
                            <form onSubmit={handleManualSubmit}>
                                <Typography variant="h5" gutterBottom sx={{ color: '#4CAF50', mb: 3 }}>
                                    Enter Soil Parameters Manually
                                </Typography>
                                <Grid container spacing={3}>
                                    <Grid item xs={12} md={6}>
                                        <TextField
                                            fullWidth
                                            label="Nitrogen (N) mg/kg"
                                            type="number"
                                            value={manualData.nitrogen}
                                            onChange={(e) => setManualData({ ...manualData, nitrogen: e.target.value })}
                                            inputProps={{ step: '0.1', min: '0' }}
                                            sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                        />
                                    </Grid>
                                    <Grid item xs={12} md={6}>
                                        <TextField
                                            fullWidth
                                            label="Phosphorus (P) mg/kg"
                                            type="number"
                                            value={manualData.phosphorus}
                                            onChange={(e) => setManualData({ ...manualData, phosphorus: e.target.value })}
                                            inputProps={{ step: '0.1', min: '0' }}
                                            sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                        />
                                    </Grid>
                                    <Grid item xs={12} md={6}>
                                        <TextField
                                            fullWidth
                                            label="Potassium (K) mg/kg"
                                            type="number"
                                            value={manualData.potassium}
                                            onChange={(e) => setManualData({ ...manualData, potassium: e.target.value })}
                                            inputProps={{ step: '0.1', min: '0' }}
                                            sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                        />
                                    </Grid>
                                    <Grid item xs={12} md={6}>
                                        <TextField
                                            fullWidth
                                            label="pH"
                                            type="number"
                                            value={manualData.ph}
                                            onChange={(e) => setManualData({ ...manualData, ph: e.target.value })}
                                            required
                                            inputProps={{ step: '0.1', min: '0', max: '14' }}
                                            sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                        />
                                    </Grid>
                                    <Grid item xs={12} md={6}>
                                        <TextField
                                            fullWidth
                                            label="EC (dS/m)"
                                            type="number"
                                            value={manualData.ec}
                                            onChange={(e) => setManualData({ ...manualData, ec: e.target.value })}
                                            inputProps={{ step: '0.01', min: '0' }}
                                            sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                        />
                                    </Grid>
                                    <Grid item xs={12} md={6}>
                                        <TextField
                                            fullWidth
                                            label="Moisture %"
                                            type="number"
                                            value={manualData.moisture}
                                            onChange={(e) => setManualData({ ...manualData, moisture: e.target.value })}
                                            inputProps={{ step: '0.1', min: '0', max: '100' }}
                                            sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            fullWidth
                                            label="Organic Carbon % (Optional)"
                                            type="number"
                                            value={manualData.organic_carbon}
                                            onChange={(e) => setManualData({ ...manualData, organic_carbon: e.target.value })}
                                            inputProps={{ step: '0.01', min: '0' }}
                                            sx={{ backgroundColor: '#2a2a2a', input: { color: '#fff' } }}
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <Button
                                            type="submit"
                                            variant="contained"
                                            fullWidth
                                            size="large"
                                            disabled={loading}
                                            sx={{ backgroundColor: '#4CAF50', '&:hover': { backgroundColor: '#45a049' } }}
                                        >
                                            {loading ? <CircularProgress size={24} /> : 'Submit & Get Recommendation'}
                                        </Button>
                                    </Grid>
                                </Grid>
                            </form>
                        )}

                        {/* File Upload Tab */}
                        {tabValue === 1 && (
                            <form onSubmit={handleFileUpload}>
                                <Typography variant="h5" gutterBottom sx={{ color: '#4CAF50', mb: 3 }}>
                                    Upload Soil Test Report
                                </Typography>
                                <Typography variant="body2" sx={{ color: '#e0e0e0', mb: 3 }}>
                                    Supported formats: CSV, Excel (.xlsx, .xls), PDF, Word (.docx), Images (JPG, PNG)
                                </Typography>
                                <Box
                                    sx={{
                                        border: '2px dashed #4CAF50',
                                        borderRadius: 2,
                                        p: 4,
                                        textAlign: 'center',
                                        backgroundColor: '#2a2a2a',
                                        mb: 3
                                    }}
                                >
                                    <CloudUploadIcon sx={{ fontSize: 60, color: '#4CAF50', mb: 2 }} />
                                    <input
                                        type="file"
                                        accept=".csv,.xlsx,.xls,.pdf,.docx,.jpg,.jpeg,.png"
                                        onChange={(e) => setSelectedFile(e.target.files[0])}
                                        style={{ display: 'none' }}
                                        id="file-upload"
                                    />
                                    <label htmlFor="file-upload">
                                        <Button variant="outlined" component="span" sx={{ borderColor: '#4CAF50', color: '#4CAF50' }}>
                                            Select File
                                        </Button>
                                    </label>
                                    {selectedFile && (
                                        <Typography sx={{ mt: 2, color: '#4CAF50' }}>
                                            Selected: {selectedFile.name}
                                        </Typography>
                                    )}
                                </Box>
                                <Button
                                    type="submit"
                                    variant="contained"
                                    fullWidth
                                    size="large"
                                    disabled={loading || !selectedFile}
                                    sx={{ backgroundColor: '#4CAF50', '&:hover': { backgroundColor: '#45a049' } }}
                                >
                                    {loading ? <CircularProgress size={24} /> : 'Upload & Extract Data'}
                                </Button>
                            </form>
                        )}
                    </CardContent>
                </Card>
            </Container>
        </Box>
    );
}

export default SoilDataEntry;
