import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import {
    Container, Typography, Card, CardContent, Button, Box, AppBar, Toolbar,
    Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
    Paper, Chip, Alert, Grid, IconButton
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import VerifiedIcon from '@mui/icons-material/Verified';
import DownloadIcon from '@mui/icons-material/Download';
import axios from 'axios';

function RecommendationView() {
    const navigate = useNavigate();
    const { farmerId, soilDataId } = useParams();
    const [farmer, setFarmer] = useState(null);
    const [soilData, setSoilData] = useState(null);
    const [recommendation, setRecommendation] = useState(null);
    const [additionalData, setAdditionalData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchData();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [farmerId, soilDataId]);

    const fetchData = async () => {
        try {
            const farmerRes = await axios.get(`/api/farmers/${farmerId}`);
            const soilRes = await axios.get(`/api/soil-data/${soilDataId}`);
            setFarmer(farmerRes.data);
            setSoilData(soilRes.data);

            const recRes = await axios.post('/api/recommendations/generate', {
                farmer_id: parseInt(farmerId),
                soil_data_id: parseInt(soilDataId),
            });
            setRecommendation(recRes.data);

            // Parse additional data from reasoning JSON
            try {
                const jsonMatch = recRes.data.reasoning.match(/\{[\s\S]*\}/);
                if (jsonMatch) {
                    const parsed = JSON.parse(jsonMatch[0]);
                    setAdditionalData(parsed);
                }
            } catch (e) {
                console.error('Failed to parse additional data:', e);
            }
        } catch (err) {
            setError(err.response?.data?.detail || 'Failed to load data');
        } finally {
            setLoading(false);
        }
    };

    const getStatus = (value, low, high, param = null) => {
        // Special handling for pH
        if (param === 'ph') {
            if (value < 5.5) return { label: 'Acidic', color: 'warning' };
            if (value > 7.5) return { label: 'Alkaline', color: 'warning' };
            return { label: 'Neutral', color: 'success' };
        }

        // Special handling for EC (salinity)
        if (param === 'ec') {
            if (value < 2) return { label: 'Normal', color: 'success' };
            if (value < 4) return { label: 'Moderate Salinity', color: 'warning' };
            return { label: 'High Salinity', color: 'error' };
        }

        // Special handling for Moisture (adjusted thresholds)
        if (param === 'moisture') {
            if (value < 20) return { label: 'Low', color: 'error' };
            if (value < 50) return { label: 'Adequate', color: 'success' };  // Changed from 35 to 50
            return { label: 'High', color: 'info' };
        }

        // Standard NPK logic with corrected thresholds
        if (value < low) return { label: 'Low', color: 'error' };
        if (value > high) return { label: 'High', color: 'warning' };
        return { label: 'Medium', color: 'success' };
    };

    const handleDownloadCSV = () => {
        // Use full backend URL to bypass React Router
        window.open(`http://localhost:8080/api/soil-health-card/${recommendation.id}/csv`, '_blank');
    };

    const handleDownloadPDF = () => {
        window.open(`http://localhost:8080/api/soil-health-card/${recommendation.id}/pdf`, '_blank');
    };

    const handleDownloadExcel = () => {
        window.open(`http://localhost:8080/api/soil-health-card/${recommendation.id}/excel`, '_blank');
    };

    if (loading) {
        return (
            <Box sx={{ minHeight: '100vh', backgroundColor: '#000000', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <Typography className="text-shimmer">Loading recommendations...</Typography>
            </Box>
        );
    }

    return (
        <Box sx={{ minHeight: '100vh', backgroundColor: '#000000' }}>
            <AppBar position="static">
                <Toolbar>
                    <img src="/logo.png" alt="SoilSense Logo"
                        style={{ height: '48px', marginRight: '16px', filter: 'drop-shadow(0 0 10px rgba(76, 175, 80, 0.5))' }} />
                    <Typography variant="h6" component="div" sx={{ flexGrow: 1, fontWeight: 600, textShadow: '0 0 10px rgba(76, 175, 80, 0.5)' }}>
                        <span className="text-shimmer">Soil Health Card</span>
                    </Typography>
                    <Button color="inherit" variant="outlined" startIcon={<DownloadIcon />}
                        onClick={handleDownloadCSV} sx={{ mr: 1, borderColor: 'rgba(76, 175, 80, 0.5)' }}>
                        CSV
                    </Button>
                    <Button color="inherit" variant="outlined" startIcon={<DownloadIcon />}
                        onClick={handleDownloadPDF} sx={{ mr: 1, borderColor: 'rgba(255, 152, 0, 0.5)' }}>
                        PDF
                    </Button>
                    <Button color="inherit" variant="outlined" startIcon={<DownloadIcon />}
                        onClick={handleDownloadExcel} sx={{ mr: 2, borderColor: 'rgba(33, 150, 243, 0.5)' }}>
                        Excel
                    </Button>
                    <Button color="inherit" startIcon={<ArrowBackIcon />} onClick={() => navigate('/')}>
                        Back to Dashboard
                    </Button>
                </Toolbar>
            </AppBar>

            <Container maxWidth="lg" sx={{ mt: 4, pb: 4 }}>
                {error && <Alert severity="error" sx={{ mb: 2, backgroundColor: 'rgba(211, 47, 47, 0.1)' }}>{error}</Alert>}

                {/* Farmer Info */}
                {farmer && (
                    <Card className="premium-card" sx={{ mb: 3 }}>
                        <CardContent>
                            <Typography variant="h5" className="text-glow" sx={{ color: '#4CAF50', mb: 1 }}>{farmer.name}</Typography>
                            <Grid container spacing={2}>
                                <Grid item xs={12} md={6}>
                                    <Typography variant="body2" sx={{ color: '#e0e0e0' }}>
                                        <strong>Location:</strong> {farmer.village}, {farmer.district}, {farmer.state}
                                    </Typography>
                                </Grid>
                                <Grid item xs={12} md={6}>
                                    <Typography variant="body2" sx={{ color: '#e0e0e0' }}>
                                        <strong>Field Area:</strong> {farmer.field_area} hectares | <strong>Crop:</strong> {farmer.crop_name} ({farmer.crop_season})
                                    </Typography>
                                </Grid>
                            </Grid>
                        </CardContent>
                    </Card>
                )}

                {/* TABLE 1: Soil Health Summary */}
                {soilData && (
                    <Card className="premium-card" sx={{ mb: 3 }}>
                        <CardContent>
                            <Typography variant="h5" className="text-shimmer" sx={{ mb: 3 }}>
                                Table 1: Soil Health Summary
                            </Typography>
                            <TableContainer component={Paper} sx={{ backgroundColor: '#000000' }}>
                                <Table>
                                    <TableHead>
                                        <TableRow sx={{ '& th': { backgroundColor: 'rgba(76, 175, 80, 0.1)', borderBottom: '2px solid rgba(76, 175, 80, 0.5)' } }}>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Parameter</TableCell>
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Value</TableCell>
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Unit</TableCell>
                                            <TableCell align="center" sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Status</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        <TableRow className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.05)' } }}>
                                            <TableCell sx={{ color: '#fff' }}>Nitrogen (N)</TableCell>
                                            <TableCell align="right" sx={{ color: '#e0e0e0' }}>{soilData.nitrogen || 'N/A'}</TableCell>
                                            <TableCell align="right" sx={{ color: '#e0e0e0' }}>mg/kg</TableCell>
                                            <TableCell align="center">
                                                <Chip label={getStatus(soilData.nitrogen || 0, 20, 40).label}
                                                    color={getStatus(soilData.nitrogen || 0, 20, 40).color} size="small" />
                                            </TableCell>
                                        </TableRow>
                                        <TableRow className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.05)' } }}>
                                            <TableCell sx={{ color: '#fff' }}>Phosphorus (P)</TableCell>
                                            <TableCell align="right" sx={{ color: '#e0e0e0' }}>{soilData.phosphorus || 'N/A'}</TableCell>
                                            <TableCell align="right" sx={{ color: '#e0e0e0' }}>mg/kg</TableCell>
                                            <TableCell align="center">
                                                <Chip label={getStatus(soilData.phosphorus || 0, 15, 35).label}
                                                    color={getStatus(soilData.phosphorus || 0, 15, 35).color} size="small" />
                                            </TableCell>
                                        </TableRow>
                                        <TableRow className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.05)' } }}>
                                            <TableCell sx={{ color: '#fff' }}>Potassium (K)</TableCell>
                                            <TableCell align="right" sx={{ color: '#e0e0e0' }}>{soilData.potassium || 'N/A'}</TableCell>
                                            <TableCell align="right" sx={{ color: '#e0e0e0' }}>mg/kg</TableCell>
                                            <TableCell align="center">
                                                <Chip label={getStatus(soilData.potassium || 0, 40, 110).label}
                                                    color={getStatus(soilData.potassium || 0, 40, 110).color} size="small" />
                                            </TableCell>
                                        </TableRow>
                                        <TableRow className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.05)' } }}>
                                            <TableCell sx={{ color: '#fff' }}>pH</TableCell>
                                            <TableCell align="right" sx={{ color: '#e0e0e0' }}>{soilData.ph || 'N/A'}</TableCell>
                                            <TableCell align="right" sx={{ color: '#e0e0e0' }}></TableCell>
                                            <TableCell align="center">
                                                <Chip label={getStatus(soilData.ph || 7, null, null, 'ph').label}
                                                    color={getStatus(soilData.ph || 7, null, null, 'ph').color} size="small" />
                                            </TableCell>
                                        </TableRow>
                                        {soilData.ec && (
                                            <TableRow className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.05)' } }}>
                                                <TableCell sx={{ color: '#fff' }}>EC (Conductivity)</TableCell>
                                                <TableCell align="right" sx={{ color: '#e0e0e0' }}>{soilData.ec}</TableCell>
                                                <TableCell align="right" sx={{ color: '#e0e0e0' }}>dS/m</TableCell>
                                                <TableCell align="center">
                                                    <Chip label={getStatus(soilData.ec, null, null, 'ec').label}
                                                        color={getStatus(soilData.ec, null, null, 'ec').color} size="small" />
                                                </TableCell>
                                            </TableRow>
                                        )}
                                        {soilData.organic_carbon && (
                                            <TableRow className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.05)' } }}>
                                                <TableCell sx={{ color: '#fff' }}>Organic Carbon</TableCell>
                                                <TableCell align="right" sx={{ color: '#e0e0e0' }}>{soilData.organic_carbon}</TableCell>
                                                <TableCell align="right" sx={{ color: '#e0e0e0' }}>%</TableCell>
                                                <TableCell align="center">
                                                    <Chip label={soilData.organic_carbon < 0.5 ? 'Low' : 'Adequate'}
                                                        color={soilData.organic_carbon < 0.5 ? 'warning' : 'success'} size="small" />
                                                </TableCell>
                                            </TableRow>
                                        )}
                                        {soilData.moisture && (
                                            <TableRow className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.05)' } }}>
                                                <TableCell sx={{ color: '#fff' }}>Moisture</TableCell>
                                                <TableCell align="right" sx={{ color: '#e0e0e0' }}>{soilData.moisture}</TableCell>
                                                <TableCell align="right" sx={{ color: '#e0e0e0' }}>%</TableCell>
                                                <TableCell align="center">
                                                    <Chip label={getStatus(soilData.moisture, null, null, 'moisture').label}
                                                        color={getStatus(soilData.moisture, null, null, 'moisture').color} size="small" />
                                                </TableCell>
                                            </TableRow>
                                        )}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </CardContent>
                    </Card>
                )}

                {/* TABLE 2: Fertilizer Recommendations */}
                {recommendation && additionalData && farmer && (
                    <Card className="premium-card" sx={{ mb: 3 }}>
                        <CardContent>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                                <Typography variant="h5" className="text-shimmer" sx={{ flexGrow: 1 }}>
                                    Table 2: Fertilizer Recommendations
                                </Typography>
                                <Chip icon={<VerifiedIcon />} label="Govt. Verified Prices" color="success" sx={{ fontWeight: 'bold' }} />
                            </Box>

                            <Box sx={{ mb: 2, p: 2, backgroundColor: 'rgba(76, 175, 80, 0.05)', borderRadius: 1 }}>
                                <Typography variant="body2" sx={{ color: '#e0e0e0' }}>
                                    <strong>Land Area:</strong> {farmer.field_area} hectares | <strong>Crop:</strong> {farmer.crop_name}
                                </Typography>
                            </Box>

                            <TableContainer component={Paper} sx={{ backgroundColor: '#000000' }}>
                                <Table>
                                    <TableHead>
                                        <TableRow sx={{ '& th': { backgroundColor: 'rgba(76, 175, 80, 0.1)', borderBottom: '2px solid rgba(76, 175, 80, 0.5)' } }}>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Fertilizer</TableCell>
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Total Quantity (kg)</TableCell>
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Govt. Price / 50kg</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {additionalData.fertilizer_details?.map((fert, index) => {
                                            const totalQuantity = fert.quantity_kg_per_hectare * farmer.field_area;
                                            const bagsNeeded = Math.ceil(totalQuantity / 50);
                                            const totalCost = bagsNeeded * fert.price_per_50kg;

                                            return (
                                                <TableRow key={index} className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.05)' } }}>
                                                    <TableCell sx={{ color: '#fff', fontWeight: 'bold' }}>{fert.fertilizer}</TableCell>
                                                    <TableCell align="right" sx={{ color: '#e0e0e0' }}>
                                                        {totalQuantity.toFixed(2)} kg
                                                        <Typography variant="caption" sx={{ display: 'block', color: '#81C784', fontSize: '0.75rem' }}>
                                                            ({bagsNeeded} bags  50kg)
                                                        </Typography>
                                                    </TableCell>
                                                    <TableCell align="right" sx={{ color: '#81C784' }}>{fert.price_per_50kg || 'N/A'}</TableCell>
                                                </TableRow>
                                            );
                                        })}

                                        <TableRow sx={{ backgroundColor: 'rgba(76, 175, 80, 0.15)', borderTop: '2px solid rgba(76, 175, 80, 0.5)' }}>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold', fontSize: '1.1rem' }}>
                                                TOTAL
                                            </TableCell>
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold', fontSize: '1.1rem' }}>
                                                {additionalData.fertilizer_details?.reduce((sum, fert) =>
                                                    sum + Math.ceil((fert.quantity_kg_per_hectare * farmer.field_area) / 50), 0
                                                )} bags
                                            </TableCell>
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold', fontSize: '1.2rem' }}>
                                                {recommendation.estimated_cost?.toLocaleString() || 'N/A'}
                                            </TableCell>
                                        </TableRow>
                                    </TableBody>
                                </Table>
                            </TableContainer>
                            <Box sx={{ mt: 2, p: 2, backgroundColor: 'rgba(76, 175, 80, 0.05)', borderRadius: 1, border: '1px solid rgba(76, 175, 80, 0.2)' }}>
                                <Typography variant="caption" sx={{ color: '#e0e0e0', display: 'flex', alignItems: 'center' }}>
                                    <VerifiedIcon sx={{ fontSize: 16, mr: 0.5, color: '#4CAF50' }} />
                                    *Prices from Dept. of Fertilizers, Govt. of India (NBS Scheme, Dec 2024). May vary by state.
                                </Typography>
                            </Box>
                        </CardContent>
                    </Card>
                )}

                {/* TABLE 3: Application Schedule */}
                {additionalData?.application_schedule && (
                    <Card className="premium-card" sx={{ mb: 3 }}>
                        <CardContent>
                            <Typography variant="h5" className="text-shimmer" sx={{ mb: 3 }}>
                                Table 3: Application Schedule
                            </Typography>
                            <TableContainer component={Paper} sx={{ backgroundColor: '#000000' }}>
                                <Table>
                                    <TableHead>
                                        <TableRow sx={{ '& th': { backgroundColor: 'rgba(76, 175, 80, 0.1)', borderBottom: '2px solid rgba(76, 175, 80, 0.5)' } }}>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Stage</TableCell>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Time</TableCell>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Fertilizer</TableCell>
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Quantity (%)</TableCell>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Note</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {additionalData.application_schedule.map((stage, index) => (
                                            <TableRow key={index} className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.05)' } }}>
                                                <TableCell sx={{ color: '#fff', fontWeight: 'bold' }}>{stage.stage}</TableCell>
                                                <TableCell sx={{ color: '#e0e0e0' }}>{stage.time}</TableCell>
                                                <TableCell sx={{ color: '#81C784' }}>{stage.fertilizer}</TableCell>
                                                <TableCell align="right" sx={{ color: '#e0e0e0' }}>{stage.quantity_percent}%</TableCell>
                                                <TableCell sx={{ color: '#e0e0e0', fontSize: '0.875rem' }}>{stage.note}</TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </CardContent>
                    </Card>
                )}

                {/* TABLE 4: Soil Improvement Advisory */}
                {additionalData?.soil_improvements && (
                    <Card className="premium-card" sx={{ mb: 3 }}>
                        <CardContent>
                            <Typography variant="h5" className="text-shimmer" sx={{ mb: 3 }}>
                                Table 4: Soil Improvement Advisory
                            </Typography>
                            <TableContainer component={Paper} sx={{ backgroundColor: '#000000' }}>
                                <Table>
                                    <TableHead>
                                        <TableRow sx={{ '& th': { backgroundColor: 'rgba(76, 175, 80, 0.1)', borderBottom: '2px solid rgba(76, 175, 80, 0.5)' } }}>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Soil Issue</TableCell>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Recommendation</TableCell>
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Quantity</TableCell>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold' }}>Purpose</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {additionalData.soil_improvements.map((imp, index) => (
                                            <TableRow key={index} className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.05)' } }}>
                                                <TableCell sx={{ color: '#fff', fontWeight: 'bold' }}>{imp.issue}</TableCell>
                                                <TableCell sx={{ color: '#81C784' }}>{imp.recommendation}</TableCell>
                                                <TableCell align="right" sx={{ color: '#e0e0e0' }}>{imp.quantity}</TableCell>
                                                <TableCell sx={{ color: '#e0e0e0', fontSize: '0.875rem' }}>{imp.purpose}</TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </CardContent>
                    </Card>
                )}

                {/* Important Notes */}
                <Card className="premium-card">
                    <CardContent>
                        <Typography variant="h5" className="text-shimmer" sx={{ mb: 2 }}>
                            Important Notes
                        </Typography>
                        <Box sx={{ p: 3, backgroundColor: 'rgba(76, 175, 80, 0.05)', borderRadius: 2, border: '1px solid rgba(76, 175, 80, 0.2)' }}>
                            <Typography variant="body2" sx={{ color: '#e0e0e0', mb: 1.5 }}>
                                 <strong>Soil Test Validity:</strong> This recommendation is valid for one cropping season.
                            </Typography>
                            <Typography variant="body2" sx={{ color: '#e0e0e0', mb: 1.5 }}>
                                 <strong>Application Timing:</strong> Follow split application schedules as shown in Table 3.
                            </Typography>
                            <Typography variant="body2" sx={{ color: '#e0e0e0', mb: 1.5 }}>
                                 <strong>Precautions:</strong> Apply during appropriate soil moisture. Avoid over-application.
                            </Typography>
                            <Typography variant="body2" sx={{ color: '#e0e0e0', mb: 1.5 }}>
                                 <strong>Organic Matter:</strong> Use FYM/compost @ 5-10 tonnes/ha for sustained soil health.
                            </Typography>
                            <Typography variant="body2" sx={{ color: '#81C784' }}>
                                 <strong>For Assistance:</strong> Contact your nearest Krishi Vigyan Kendra (KVK) or agricultural extension officer.
                            </Typography>
                        </Box>
                    </CardContent>
                </Card>
            </Container>
        </Box>
    );
}

export default RecommendationView;
