import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import {
    Container, Typography, Card, CardContent, Button, Box, AppBar, Toolbar,
    Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
    Paper, Chip, Alert, Grid, CircularProgress
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import VerifiedIcon from '@mui/icons-material/Verified';
import DownloadIcon from '@mui/icons-material/Download';
import axios from 'axios';

function RecommendationView() {
    const navigate = useNavigate();
    const { farmerId, soilDataId } = useParams();
    const [farmer, setFarmer] = useState(null);
    const [soilData, setSoilData] = useState(null);
    const [recommendation, setRecommendation] = useState(null);
    const [additionalData, setAdditionalData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchData();
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [farmerId, soilDataId]);

    const fetchData = async () => {
        try {
            const farmerRes = await axios.get(`/api/farmers/${farmerId}`);
            const soilRes = await axios.get(`/api/soil-data/${soilDataId}`);
            setFarmer(farmerRes.data);
            setSoilData(soilRes.data);

            const recRes = await axios.post('/api/recommendations/generate', {
                farmer_id: parseInt(farmerId),
                soil_data_id: parseInt(soilDataId),
            });
            setRecommendation(recRes.data);

            // Parse additional data from reasoning JSON
            try {
                const jsonMatch = recRes.data.reasoning.match(/\{[\s\S]*\}/);
                if (jsonMatch) {
                    const parsed = JSON.parse(jsonMatch[0]);
                    setAdditionalData(parsed);
                }
            } catch (e) {
                console.error('Failed to parse additional data:', e);
            }
        } catch (err) {
            setError(err.response?.data?.detail || 'Failed to load data');
        } finally {
            setLoading(false);
        }
    };

    const getStatus = (value, low, high, param = null) => {
        // Special handling for pH
        if (param === 'ph') {
            if (value < 5.5) return { label: 'Acidic', color: 'warning', percent: 30 };
            if (value > 7.5) return { label: 'Alkaline', color: 'warning', percent: 100 };
            return { label: 'Neutral', color: 'success', percent: 60 };
        }

        // Special handling for EC (salinity)
        if (param === 'ec') {
            if (value < 2) return { label: 'Normal', color: 'success', percent: 25 };
            if (value < 4) return { label: 'Moderate', color: 'warning', percent: 60 };
            return { label: 'High', color: 'error', percent: 100 };
        }

        // Special handling for Moisture
        if (param === 'moisture') {
            if (value < 20) return { label: 'Low', color: 'error', percent: value };
            if (value < 50) return { label: 'Adequate', color: 'success', percent: value };
            return { label: 'High', color: 'info', percent: value };
        }

        // Standard NPK logic
        // Calculate rough percentage relative to 'high' threshold for visualization
        let percent = (value / (high * 1.5)) * 100;
        if (percent > 100) percent = 100;

        if (value < low) return { label: 'Low', color: 'error', percent: percent };
        if (value > high) return { label: 'High', color: 'warning', percent: percent };
        return { label: 'Medium', color: 'success', percent: percent };
    };

    // Helper for Circular Gauge
    const CircularGauge = ({ value, color, label }) => {
        // Map color string to hex
        const colorMap = {
            success: '#4CAF50',
            warning: '#FF9800',
            error: '#F44336',
            info: '#2196F3'
        };
        const hexColor = colorMap[color] || '#4CAF50';

        return (
            <Box sx={{ position: 'relative', display: 'inline-flex', flexDirection: 'column', alignItems: 'center' }}>
                <Box sx={{ position: 'relative', display: 'inline-flex' }}>
                    <CircularProgress
                        variant="determinate"
                        value={100}
                        size={50}
                        sx={{ color: 'rgba(255, 255, 255, 0.1)' }}
                    />
                    <CircularProgress
                        variant="determinate"
                        value={value}
                        size={50}
                        sx={{
                            color: hexColor,
                            position: 'absolute',
                            left: 0,
                            '& .MuiCircularProgress-circle': { strokeLinecap: 'round' }
                        }}
                    />
                    <Box
                        sx={{
                            top: 0,
                            left: 0,
                            bottom: 0,
                            right: 0,
                            position: 'absolute',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                        }}
                    >
                        <Typography variant="caption" component="div" sx={{ color: hexColor, fontWeight: 'bold' }}>
                            {Math.round(value)}%
                        </Typography>
                    </Box>
                </Box>
                <Typography variant="caption" sx={{ mt: 0.5, color: hexColor, fontWeight: 600 }}>
                    {label}
                </Typography>
            </Box>
        );
    };

    const handleDownloadCSV = () => {
        window.open(`http://localhost:8080/api/soil-health-card/${recommendation.id}/csv`, '_blank');
    };

    const handleDownloadPDF = () => {
        window.open(`http://localhost:8080/api/soil-health-card/${recommendation.id}/pdf`, '_blank');
    };

    const handleDownloadExcel = () => {
        window.open(`http://localhost:8080/api/soil-health-card/${recommendation.id}/excel`, '_blank');
    };

    if (loading) {
        return (
            <Box sx={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                <CircularProgress size={60} sx={{ color: '#4CAF50' }} />
                <Typography className="text-shimmer" sx={{ ml: 2 }}>Loading Analysis...</Typography>
            </Box>
        );
    }

    return (
        <Box sx={{ minHeight: '100vh', pb: 8 }}>
            <AppBar position="static" elevation={0} sx={{ backgroundColor: 'transparent' }}>
                <Toolbar>
                    <img src="/logo.png" alt="SoilSense Logo"
                        style={{ height: '60px', marginRight: '16px' }} className="neon-icon" />
                    <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                        <span className="text-shimmer" style={{ fontSize: '1.5rem' }}>Soil Health Card</span>
                    </Typography>
                    <Button color="inherit" variant="outlined" startIcon={<DownloadIcon className="neon-icon" />}
                        onClick={handleDownloadCSV} sx={{ mr: 1, borderColor: 'rgba(76, 175, 80, 0.5)', borderRadius: '20px' }}>
                        CSV
                    </Button>
                    <Button color="inherit" variant="outlined" startIcon={<DownloadIcon className="neon-icon" />}
                        onClick={handleDownloadPDF} sx={{ mr: 1, borderColor: 'rgba(255, 152, 0, 0.5)', borderRadius: '20px' }}>
                        PDF
                    </Button>
                    <Button color="inherit" variant="outlined" startIcon={<DownloadIcon className="neon-icon" />}
                        onClick={handleDownloadExcel} sx={{ mr: 2, borderColor: 'rgba(33, 150, 243, 0.5)', borderRadius: '20px' }}>
                        Excel
                    </Button>
                    <Button color="inherit" startIcon={<ArrowBackIcon />} onClick={() => navigate('/')} sx={{ borderRadius: '20px' }}>
                        Dashboard
                    </Button>
                </Toolbar>
            </AppBar>

            <Container maxWidth="lg" sx={{ mt: 4 }} component={motion.div} initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5 }}>
                {error && <Alert severity="error" sx={{ mb: 2, backgroundColor: 'rgba(211, 47, 47, 0.1)', backdropFilter: 'blur(10px)' }}>{typeof error === 'string' ? error : 'Failed to load data.'}</Alert>}

                {/* Farmer Info */}
                {farmer && (
                    <Card className="premium-card" sx={{ mb: 4 }}>
                        <CardContent>
                            <Typography variant="h4" className="text-shimmer" sx={{ mb: 1 }}>{farmer.name}</Typography>
                            <Grid container spacing={2}>
                                <Grid item xs={12} md={6}>
                                    <Typography variant="body1" sx={{ color: '#e0e0e0', fontSize: '1.1rem' }}>
                                         {farmer.village}, {farmer.district}, {farmer.state}
                                    </Typography>
                                </Grid>
                                <Grid item xs={12} md={6}>
                                    <Typography variant="body1" sx={{ color: '#e0e0e0', fontSize: '1.1rem' }}>
                                         <strong>{farmer.field_area} ha</strong> | {farmer.crop_name} ({farmer.crop_season})
                                    </Typography>
                                </Grid>
                            </Grid>
                        </CardContent>
                    </Card>
                )}

                {/* TABLE 1: Soil Health Summary with GAUGES */}
                {soilData && (
                    <Card className="premium-card" sx={{ mb: 4 }}>
                        <CardContent>
                            <Typography variant="h5" sx={{ mb: 3, color: '#4CAF50', fontWeight: 'bold' }}>
                                 Soil Health Analysis
                            </Typography>
                            <TableContainer component={Paper} sx={{ backgroundColor: 'transparent', boxShadow: 'none' }}>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell sx={{ color: '#81C784', fontWeight: 'bold', fontSize: '1.1rem' }}>Parameter</TableCell>
                                            <TableCell align="right" sx={{ color: '#81C784', fontWeight: 'bold', fontSize: '1.1rem' }}>Value</TableCell>
                                            <TableCell align="center" sx={{ color: '#81C784', fontWeight: 'bold', fontSize: '1.1rem' }}>Health Score</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {[
                                            { label: 'Nitrogen (N)', value: soilData.nitrogen, unit: 'mg/kg', param: 'nitrogen', low: 20, high: 40 },
                                            { label: 'Phosphorus (P)', value: soilData.phosphorus, unit: 'mg/kg', param: 'phosphorus', low: 15, high: 35 },
                                            { label: 'Potassium (K)', value: soilData.potassium, unit: 'mg/kg', param: 'potassium', low: 40, high: 110 },
                                            { label: 'pH Level', value: soilData.ph, unit: '', param: 'ph', low: 5.5, high: 7.5 },
                                            { label: 'EC (Salinity)', value: soilData.ec, unit: 'dS/m', param: 'ec', low: 0, high: 0 },
                                            { label: 'Moisture', value: soilData.moisture, unit: '%', param: 'moisture', low: 0, high: 0 },
                                        ].map((row, idx) => {
                                            if (row.value === null || row.value === undefined) return null;
                                            const status = getStatus(row.value, row.low, row.high, row.param);
                                            return (
                                                <TableRow key={idx} className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.1)' } }}>
                                                    <TableCell sx={{ color: '#fff', fontSize: '1.1rem', py: 2 }}>{row.label}</TableCell>
                                                    <TableCell align="right" sx={{ color: '#fff', fontSize: '1.2rem', fontWeight: 600 }}>
                                                        {row.value} {row.unit && <span style={{ fontSize: '0.9rem', color: '#aaa' }}>{row.unit}</span>}
                                                    </TableCell>
                                                    <TableCell align="center">
                                                        <CircularGauge value={status.percent} color={status.color} label={status.label} />
                                                    </TableCell>
                                                </TableRow>
                                            );
                                        })}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </CardContent>
                    </Card>
                )}

                {/* TABLE 2: Fertilizer Recommendations */}
                {recommendation && additionalData && (
                    <Card className="premium-card" sx={{ mb: 4 }}>
                        <CardContent>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                                <Typography variant="h5" sx={{ flexGrow: 1, color: '#4CAF50', fontWeight: 'bold' }}>
                                     Fertilizer Plan
                                </Typography>
                                <Chip icon={<VerifiedIcon className="neon-icon" />} label="Govt. Verified Prices"
                                    sx={{ backgroundColor: 'rgba(76, 175, 80, 0.2)', color: '#4CAF50', fontWeight: 'bold' }} />
                            </Box>

                            <TableContainer component={Paper} sx={{ backgroundColor: 'transparent', boxShadow: 'none' }}>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell sx={{ color: '#81C784', fontWeight: 'bold' }}>Fertilizer Product</TableCell>
                                            <TableCell align="right" sx={{ color: '#81C784', fontWeight: 'bold' }}>Total Quantity</TableCell>
                                            <TableCell align="right" sx={{ color: '#81C784', fontWeight: 'bold' }}>Bags (50kg)</TableCell>
                                            <TableCell align="right" sx={{ color: '#81C784', fontWeight: 'bold' }}>Cost Estimate</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {additionalData.fertilizer_details?.map((fert, index) => {
                                            const totalQuantity = fert.quantity_kg_per_hectare * farmer.field_area;
                                            const bagsRequired = Math.ceil(totalQuantity / 50);

                                            return (
                                                <TableRow key={index} className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.1)' } }}>
                                                    <TableCell sx={{ color: '#fff', fontSize: '1.1rem', fontWeight: 500 }}>{fert.fertilizer}</TableCell>
                                                    <TableCell align="right" sx={{ color: '#e0e0e0', fontSize: '1.1rem' }}>
                                                        {totalQuantity.toFixed(2)} <span style={{ fontSize: '0.8rem', color: '#888' }}>kg</span>
                                                    </TableCell>
                                                    <TableCell align="right" sx={{ color: '#4CAF50', fontSize: '1.1rem', fontWeight: 'bold' }}>
                                                        {bagsRequired}
                                                    </TableCell>
                                                    <TableCell align="right" sx={{ color: '#81C784', fontSize: '1.1rem' }}>
                                                        {fert.price_per_50kg}
                                                    </TableCell>
                                                </TableRow>
                                            );
                                        })}

                                        <TableRow sx={{ backgroundColor: 'rgba(76, 175, 80, 0.2)' }}>
                                            <TableCell sx={{ color: '#4CAF50', fontWeight: 'bold', fontSize: '1.2rem' }}>TOTAL ESTIMATE</TableCell>
                                            <TableCell colSpan={2} />
                                            <TableCell align="right" sx={{ color: '#4CAF50', fontWeight: 'bold', fontSize: '1.5rem', textShadow: '0 0 10px rgba(76, 175, 80, 0.5)' }}>
                                                {recommendation.estimated_cost?.toLocaleString()}
                                            </TableCell>
                                        </TableRow>
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </CardContent>
                    </Card>
                )}

                {/* TABLE 3: Application Schedule */}
                {additionalData?.application_schedule && (
                    <Card className="premium-card" sx={{ mb: 4 }} component={motion.div} initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: 0.3 }}>
                        <CardContent>
                            <Typography variant="h5" sx={{ mb: 3, color: '#4CAF50', fontWeight: 'bold' }}>
                                 Application Schedule
                            </Typography>
                            <TableContainer component={Paper} sx={{ backgroundColor: 'transparent', boxShadow: 'none' }}>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell sx={{ color: '#81C784', fontWeight: 'bold' }}>Stage</TableCell>
                                            <TableCell sx={{ color: '#81C784', fontWeight: 'bold' }}>Time</TableCell>
                                            <TableCell sx={{ color: '#81C784', fontWeight: 'bold' }}>Fertilizer</TableCell>
                                            <TableCell align="right" sx={{ color: '#81C784', fontWeight: 'bold' }}>Quantity (%)</TableCell>
                                            <TableCell sx={{ color: '#81C784', fontWeight: 'bold' }}>Note</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {additionalData.application_schedule.map((stage, index) => (
                                            <TableRow key={index} className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.1)' } }}>
                                                <TableCell sx={{ color: '#fff', fontWeight: 500 }}>{stage.stage}</TableCell>
                                                <TableCell sx={{ color: '#e0e0e0' }}>{stage.time}</TableCell>
                                                <TableCell sx={{ color: '#81C784' }}>{stage.fertilizer}</TableCell>
                                                <TableCell align="right" sx={{ color: '#e0e0e0' }}>{stage.quantity_percent}%</TableCell>
                                                <TableCell sx={{ color: '#e0e0e0', fontSize: '0.9rem' }}>{stage.note}</TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </CardContent>
                    </Card>
                )}

                {/* TABLE 4: Soil Improvement Advisory */}
                {additionalData?.soil_improvements && (
                    <Card className="premium-card" sx={{ mb: 4 }} component={motion.div} initial={{ opacity: 0, x: 20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: 0.4 }}>
                        <CardContent>
                            <Typography variant="h5" sx={{ mb: 3, color: '#4CAF50', fontWeight: 'bold' }}>
                                 Soil Improvement Advisory
                            </Typography>
                            <TableContainer component={Paper} sx={{ backgroundColor: 'transparent', boxShadow: 'none' }}>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell sx={{ color: '#81C784', fontWeight: 'bold' }}>Soil Issue</TableCell>
                                            <TableCell sx={{ color: '#81C784', fontWeight: 'bold' }}>Recommendation</TableCell>
                                            <TableCell align="right" sx={{ color: '#81C784', fontWeight: 'bold' }}>Quantity</TableCell>
                                            <TableCell sx={{ color: '#81C784', fontWeight: 'bold' }}>Purpose</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {additionalData.soil_improvements.map((imp, index) => (
                                            <TableRow key={index} className="border-glow" sx={{ '&:hover': { backgroundColor: 'rgba(76, 175, 80, 0.1)' } }}>
                                                <TableCell sx={{ color: '#fff', fontWeight: 500 }}>{imp.issue}</TableCell>
                                                <TableCell sx={{ color: '#81C784' }}>{imp.recommendation}</TableCell>
                                                <TableCell align="right" sx={{ color: '#e0e0e0' }}>{imp.quantity}</TableCell>
                                                <TableCell sx={{ color: '#e0e0e0', fontSize: '0.9rem' }}>{imp.purpose}</TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </CardContent>
                    </Card>
                )}

            </Container>
        </Box>
    );
}

export default RecommendationView;
