import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
    Container,
    Typography,
    Button,
    Grid,
    Card,
    CardContent,
    Box,
    AppBar,
    Toolbar,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
    Chip,
    CircularProgress,
    Alert,
} from '@mui/material';
import AgricultureIcon from '@mui/icons-material/Agriculture';
import DownloadIcon from '@mui/icons-material/Download';
import axios from 'axios';

function RecommendationView() {
    const { farmerId, soilDataId } = useParams();
    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [farmer, setFarmer] = useState(null);
    const [soilData, setSoilData] = useState(null);
    const [recommendation, setRecommendation] = useState(null);
    const [soilHealthCard, setSoilHealthCard] = useState(null);

    useEffect(() => {
        fetchAllData();
    }, [farmerId, soilDataId]);

    const fetchAllData = async () => {
        try {
            const [farmerRes, soilRes] = await Promise.all([
                axios.get(`/api/farmers/${farmerId}`),
                axios.get(`/api/soil-data/${soilDataId}`),
            ]);

            setFarmer(farmerRes.data);
            setSoilData(soilRes.data);

            // Generate recommendation
            const recRes = await axios.post('/api/recommendations/generate', {
                farmer_id: parseInt(farmerId),
                soil_data_id: parseInt(soilDataId),
            });
            setRecommendation(recRes.data);

            // Generate Soil Health Card
            const cardRes = await axios.post('/api/soil-health-card/generate', {
                farmer_id: parseInt(farmerId),
                soil_data_id: parseInt(soilDataId),
            });
            setSoilHealthCard(cardRes.data);

        } catch (err) {
            setError(err.response?.data?.detail || 'Error loading data');
        } finally {
            setLoading(false);
        }
    };

    const handleDownload = async (format) => {
        if (!soilHealthCard) return;

        try {
            const response = await axios.get(
                `/api/soil-health-card/${soilHealthCard.card_id}/download/${format}`,
                { responseType: 'blob' }
            );

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `soil_health_card_${soilHealthCard.card_id}.${format === 'excel' ? 'xlsx' : format}`);
            document.body.appendChild(link);
            link.click();
            link.remove();
        } catch (err) {
            console.error('Download error:', err);
        }
    };

    if (loading) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '100vh', backgroundColor: '#000' }}>
                <CircularProgress sx={{ color: '#4CAF50' }} />
            </Box>
        );
    }

    if (error) {
        return (
            <Box sx={{ minHeight: '100vh', backgroundColor: '#000', p: 4 }}>
                <Alert severity="error">{error}</Alert>
            </Box>
        );
    }

    return (
        <Box sx={{ minHeight: '100vh', backgroundColor: '#000000' }}>
            <AppBar position="static" sx={{ backgroundColor: '#1a1a1a' }}>
                <Toolbar>
                    <AgricultureIcon sx={{ mr: 2, color: '#4CAF50' }} />
                    <Typography variant="h6" component="div" sx={{ flexGrow: 1, color: '#4CAF50' }}>
                        SoilSense - Fertilizer Recommendation
                    </Typography>
                    <Button sx={{ color: '#4CAF50' }} onClick={() => navigate('/')}>
                        Back to Dashboard
                    </Button>
                </Toolbar>
            </AppBar>

            <Container maxWidth="lg" sx={{ mt: 4, pb: 4 }}>
                {/* Farmer Info */}
                {farmer && (
                    <Card sx={{ mb: 3, backgroundColor: '#1a1a1a' }}>
                        <CardContent>
                            <Grid container spacing={2}>
                                <Grid item xs={12} md={6}>
                                    <Typography variant="h6" sx={{ color: '#4CAF50' }}>
                                        {farmer.name}
                                    </Typography>
                                    <Typography variant="body2" sx={{ color: '#e0e0e0' }}>
                                        {farmer.village}, {farmer.district}, {farmer.state}
                                    </Typography>
                                </Grid>
                                <Grid item xs={12} md={6}>
                                    <Typography variant="body2" sx={{ color: '#e0e0e0' }}>
                                        <strong>Crop:</strong> {farmer.crop_name} ({farmer.crop_season})
                                    </Typography>
                                    <Typography variant="body2" sx={{ color: '#e0e0e0' }}>
                                        <strong>Field Area:</strong> {farmer.field_area} hectares
                                    </Typography>
                                </Grid>
                            </Grid>
                        </CardContent>
                    </Card>
                )}

                {/* Soil Health Card Downloads */}
                {soilHealthCard && (
                    <Card sx={{ mb: 3, backgroundColor: '#1a1a1a' }}>
                        <CardContent>
                            <Typography variant="h5" gutterBottom sx={{ color: '#4CAF50' }}>
                                Soil Health Card (ID: {soilHealthCard.card_id})
                            </Typography>
                            <Grid container spacing={2} sx={{ mt: 1 }}>
                                <Grid item>
                                    <Button
                                        variant="contained"
                                        startIcon={<DownloadIcon />}
                                        onClick={() => handleDownload('pdf')}
                                        sx={{ backgroundColor: '#4CAF50', '&:hover': { backgroundColor: '#45a049' } }}
                                    >
                                        Download PDF
                                    </Button>
                                </Grid>
                                <Grid item>
                                    <Button
                                        variant="outlined"
                                        startIcon={<DownloadIcon />}
                                        onClick={() => handleDownload('excel')}
                                        sx={{ borderColor: '#4CAF50', color: '#4CAF50' }}
                                    >
                                        Download Excel
                                    </Button>
                                </Grid>
                                <Grid item>
                                    <Button
                                        variant="outlined"
                                        startIcon={<DownloadIcon />}
                                        onClick={() => handleDownload('csv')}
                                        sx={{ borderColor: '#4CAF50', color: '#4CAF50' }}
                                    >
                                        Download CSV
                                    </Button>
                                </Grid>
                                <Grid item>
                                    <Button
                                        variant="outlined"
                                        startIcon={<DownloadIcon />}
                                        onClick={() => handleDownload('json')}
                                        sx={{ borderColor: '#4CAF50', color: '#4CAF50' }}
                                    >
                                        Download JSON
                                    </Button>
                                </Grid>
                            </Grid>
                        </CardContent>
                    </Card>
                )}

                {/* Fertilizer Recommendation */}
                {recommendation && (
                    <Card sx={{ mb: 3, backgroundColor: '#1a1a1a' }}>
                        <CardContent>
                            <Typography variant="h5" gutterBottom sx={{ color: '#4CAF50' }}>
                                Fertilizer Recommendation
                            </Typography>

                            <TableContainer component={Paper} sx={{ mt: 2, backgroundColor: '#2a2a2a' }}>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                Fertilizer
                                            </TableCell>
                                            <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                Quantity (kg/hectare)
                                            </TableCell>
                                            <TableCell sx={{ color: '#fff', fontWeight: 'bold', backgroundColor: '#4CAF50' }}>
                                                Quantity (kg/acre)
                                            </TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        <TableRow>
                                            <TableCell sx={{ color: '#e0e0e0' }}>{recommendation.fertilizer_name}</TableCell>
                                            <TableCell sx={{ color: '#e0e0e0' }}>{recommendation.quantity_kg_per_hectare}</TableCell>
                                            <TableCell sx={{ color: '#e0e0e0' }}>{recommendation.quantity_kg_per_acre}</TableCell>
                                        </TableRow>
                                    </TableBody>
                                </Table>
                            </TableContainer>

                            <Box sx={{ mt: 3, p: 2, backgroundColor: '#2a2a2a', borderRadius: 1 }}>
                                <Typography variant="h6" sx={{ color: '#4CAF50', mb: 2 }}>
                                    Estimated Cost
                                </Typography>
                                <Typography variant="h4" sx={{ color: '#fff' }}>
                                    â‚¹{recommendation.estimated_cost}
                                </Typography>
                            </Box>

                            <Box sx={{ mt: 3, p: 2, backgroundColor: '#2a2a2a', borderRadius: 1 }}>
                                <Typography variant="h6" sx={{ color: '#4CAF50', mb: 2 }}>
                                    Analysis & Reasoning
                                </Typography>
                                <Typography variant="body2" sx={{ color: '#e0e0e0', whiteSpace: 'pre-line' }}>
                                    {recommendation.reasoning}
                                </Typography>
                            </Box>
                        </CardContent>
                    </Card>
                )}
            </Container>
        </Box>
    );
}

export default RecommendationView;
